import React, { useState } from 'react';

const SchemaPopup = () => {
  const options = [
    { label: 'First Name', value: 'first_name' },
    { label: 'Last Name', value: 'last_name' },
    { label: 'Gender', value: 'gender' },
    { label: 'Age', value: 'age' },
    { label: 'Account Name', value: 'account_name' },
    { label: 'City', value: 'city' },
    { label: 'State', value: 'state' }
  ];

  const [selectedOption, setSelectedOption] = useState('');
  const [addedSchemas, setAddedSchemas] = useState([]);

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleAddNewSchema = () => {
    if (selectedOption) {
      setAddedSchemas([...addedSchemas, selectedOption]);
      setSelectedOption('');
    }
  };

  const availableOptions = options.filter(option => !addedSchemas.includes(option.value));

  return (
    <div className="schema-container">
        <select value={selectedOption} onChange={handleOptionChange}>
          <option value="">Select an option</option>
          {availableOptions.map(option => (
            <option key={option.value} value={option.value}>
              {`Label: ${option.label} Value: ${option.value}`}
            </option>
          ))}
        </select>
        <button onClick={handleAddNewSchema}>+ Add new schema</button>

        {addedSchemas.length > 0 && (
          <div className="blue-box">
            {addedSchemas.map((schema, index) => (
              <select key={index}>
                <option value={schema}>{schema}</option>
              </select>
            ))}
          </div>
        )}
      </div>
    );
};

export default SchemaPopup;
