import React, { useState } from 'react';
import "../styles/header.css";
import MinusIcon from '../assets/minus-icon.svg'

const SchemaPopup = () => {
  const options = [
    { label: 'First Name', value: 'first_name' },
    { label: 'Last Name', value: 'last_name' },
    { label: 'Gender', value: 'gender' },
    { label: 'Age', value: 'age' },
    { label: 'Account Name', value: 'account_name' },
    { label: 'City', value: 'city' },
    { label: 'State', value: 'state' }
  ];

  const [selectedOption, setSelectedOption] = useState('');
  const [addedSchemas, setAddedSchemas] = useState([]);

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleAddNewSchema = () => {
    if (selectedOption) {
      setAddedSchemas([...addedSchemas, selectedOption]);
      setSelectedOption('');
    }
  };
  const getDotClassName = (schema) => {
    const greenSchemas = ['account_name', 'city', 'state'];
    return greenSchemas.includes(schema) ? 'green' : 'red';
  };

  const availableOptions = options.filter(option => !addedSchemas.includes(option.value));

  return (
    <div className="schema-container">
        {addedSchemas.length > 0 && (
            <div className="blue-box">
                {addedSchemas.map((schema, index) => (
                <div className='selected-schemas' key={index}>
                    <span className={`dot ${getDotClassName(schema)}`} />    
                <select className='select-schema-option'>
                    <option value={schema}>{schema}</option>
                </select>
                <img className="minus-icon" src={MinusIcon} alt="MinusIcon" />

                </div>
                ))}
            </div>
        )}


        <div className='schema-segment'>
        <span className="dot gray"/>
        <select className='select-schema-option' value={selectedOption} onChange={handleOptionChange}>
          <option  value="">Add schema to segment</option>
          {availableOptions.map(option => (
            <option className={option.label} key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
        <img className="minus-icon" src={MinusIcon} alt="MinusIcon" />
        </div>
        <a className='addNew-schema' onClick={handleAddNewSchema}>+ Add new schema</a>


      </div>
    );
};

export default SchemaPopup;
