import React, { useState } from 'react';
import "../styles/header.css";

const SchemaPopup = () => {
  const options = [
    { label: 'First Name', value: 'first_name', trait: "user" },
    { label: 'Last Name', value: 'last_name', trait: "user" },
    { label: 'Gender', value: 'gender', trait: "user" },
    { label: 'Age', value: 'age', trait: "user" },
    { label: 'Account Name', value: 'account_name', trait: "group" },
    { label: 'City', value: 'city', trait: "group" },
    { label: 'State', value: 'state', trait: "group" }
  ];

  const [selectedOption, setSelectedOption] = useState({value:'', trait:'' });
  const [addedSchemas, setAddedSchemas] = useState([]);


  const handleOptionChange = (event) => {
    console.log(event.target.value, "event")
    setSelectedOption({value:event.target.value, trait:event.target.name});
  };

  const handleAddNewSchema = () => {
    if (selectedOption) {
      setAddedSchemas([...addedSchemas, selectedOption.value, selectedOption.trait]);
      setSelectedOption({});
    }
  };

  const availableOptions = options.filter(option => !addedSchemas.includes(option.value && option.trait));

  return (
    <div className="schema-container">
        {addedSchemas.length > 0 && (
            <div className="blue-box">
                {addedSchemas.map((schema, index) => (
                <div className='selected-schemas'>
                    <span className={`dot ${schema.trait=== 'user' ? 'green' : 'red'}`} />    
                <select className='select-schema-option' key={index}>
                    <option value={schema}>{schema}</option>
                </select>
                </div>
                ))}
            </div>
        )}


        <div className='schema-segment'>
        <select className='select-schema-option' value={selectedOption.value} onChange={handleOptionChange}>
          <option  value="">Add schema to segment</option>
          {availableOptions.map(option => (
            <option className={option.label} key={option.value} value={option.value} name={option.trait}>
              {option.label}
            </option>
          ))}
        </select>
        </div>
        <a className='addNew-schema' onClick={handleAddNewSchema}>+ Add new schema</a>


      </div>
    );
};

export default SchemaPopup;
